import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import * as Constants from '../constants';

class EditCampaignModal extends React.Component {
    constructor(props){
        super(props);
        this.updateCampaign = this.updateCampaign.bind(this)
    }

    updateCampaign() {
        fetch(Constants.CAMPAIGN_URL+this.props.campaign.id, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(this.props.campaign)
        }).then((response) => {
            return response.text();
        }).then(data => {
            this.setState({
                campaign: {
                    name: "",
                    description: ""
                }
            })
            this.props.onHide()
            this.props.getCampaigns()
            this.props.onShow()
            //success adding client
        })
    }

    // handleInputChange = e => {
    //     console.log(e)
    //     let change = {}
    //     change = this.props.client
    //     change[e.target.name] = e.target.value
    //     this.setState({
    //         client: change
    //     })
    // }

    // handleDateChange = e => {
    //     console.log(e)
    //     let change = {}
    //     change = this.state.client
    //     change["birthdate"] = e
    //     this.setState({
    //         client: change
    //     })
    // }

    render() {
        console.log(this.props.client)
        return(
            <Modal {...this.props} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Editar Campaña
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h4>Ingrese los datos de la campaña a editar</h4>
                    <p>
                    <Form>
                        <Form.Row className="align-items-center">
                            <Col sm={12} className="my-1">
                                <Form.Label htmlFor="inlineFormInputGroupUsername" srOnly>
                                    Nombre
                                </Form.Label>
                                <InputGroup>
                                    <InputGroup.Prepend>
                                        <InputGroup.Text>Nombre</InputGroup.Text>
                                    </InputGroup.Prepend>
                                    <FormControl name="name" onChange={this.props.handleInputChange} value={this.props.campaign.name} placeholder="Ingrese el nombre de la campaña" />
                                </InputGroup>
                            </Col>
                        </Form.Row>
                        <br></br>
                        <Form.Row className="align-items-center">
                            <Col sm={12} className="my-1">
                                <Form.Label htmlFor="inlineFormInputGroupUsername" srOnly>
                                    Descripción
                                </Form.Label>
                                <InputGroup>
                                    <InputGroup.Prepend>
                                        <InputGroup.Text>Descripción</InputGroup.Text>
                                    </InputGroup.Prepend>
                                    <FormControl as="textarea" name="description" onChange={this.props.handleInputChange} value={this.props.campaign.description} placeholder="Ingrese la descripción de la campaña" />
                                </InputGroup>
                            </Col>
                        </Form.Row>
                    </Form>
                    </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={this.updateCampaign}>Editar</Button>
                    <Button variant="danger" onClick={this.props.onHide}>Cancelar</Button>
                </Modal.Footer>
            </Modal>
        );
    }

    componentDidMount() {
        // let formatedClient = {}
        // formatedClient = this.props.client
        // formatedClient["birthdate"] = new Date(formatedClient["birthdate"]);
        this.setState({campaign: this.props.campaign})

    }
}

export default EditCampaignModal;
