import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import * as Constants from '../constants';

class AddCampaignModal extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            campaign: {
                name: "",
                description: "",
            },
            provinces: [],
            cantons: []
        }
        this.saveCampaign = this.saveCampaign.bind(this)
    }

    saveCampaign() {
        fetch(Constants.CAMPAIGN_URL, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(this.state.campaign)
        }).then((response) => {
            return response.json();
        }).then(data => {
            this.setState({
                campaign: {
                    name: "",
                    description: ""
                }
            })
            this.props.onHide()
            this.props.getCampaigns()
            this.props.setAlertState(true)
            this.props.showAlert()
            //success adding client
        }).catch(e => {
            console.log(e)
            this.props.setAlertState(false)
        })
    }

    handleInputChange = e => {
        console.log(e)
        let change = {}
        change = this.state.campaign
        change[e.target.name] = e.target.value
        this.setState({
            campaign: change
        })
    }

    render() {
        return(
            <Modal {...this.props} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Nueva Campaña
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h4>Ingrese los datos de la campaña a registrar</h4>
                    <p>
                    <Form>
                        <Form.Row className="align-items-center">
                            <Col sm={12} className="my-1">
                                <Form.Label htmlFor="inlineFormInputGroupUsername" srOnly>
                                    Nombre
                                </Form.Label>
                                <InputGroup>
                                    <InputGroup.Prepend>
                                        <InputGroup.Text>Nombre</InputGroup.Text>
                                    </InputGroup.Prepend>
                                    <FormControl name="name" onChange={this.handleInputChange} value={this.state.campaign.name} placeholder="Ingrese el nombre de la campaña" />
                                </InputGroup>
                            </Col>
                        </Form.Row>
                        <br></br>
                        <Form.Row className="align-items-center">
                            <Col sm={12} className="my-1">
                                <Form.Label htmlFor="inlineFormInputGroupUsername" srOnly>
                                    Descripción
                                </Form.Label>
                                <InputGroup>
                                    <InputGroup.Prepend>
                                        <InputGroup.Text>Descripción</InputGroup.Text>
                                    </InputGroup.Prepend>
                                    <FormControl as="textarea" name="description" onChange={this.handleInputChange} value={this.state.campaign.description} placeholder="Ingrese la descripción de la campaña" />
                                </InputGroup>
                            </Col>
                        </Form.Row>
                    </Form>
                    </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={this.saveCampaign}>Agregar</Button>
                    <Button variant="danger" onClick={this.props.onHide}>Cancelar</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}

export default AddCampaignModal;
