import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import * as Constants from '../constants';
import ContactClientsTable from '../Client/ContactClientsTable';
import '../css/AsignClients.css'


class ContactClientsModal extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            contactability: {
                idClient: 0,
                idCampaign: 0,
                anser: false
            },
            provinces: [],
            cantons: [],
        }
        this.updateContact = this.updateContact.bind(this)
    }

    updateContact() {
        fetch(Constants.CONTACTABILITY_URL, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(this.state.contactability)
        }).then((response) => {
            return response.json();
        }).then(data => {
            this.setState({
                contactability: {
                    idClient: 0,
                    idCampaign: 0,
                    anser: false
                }
            })
            //success adding client
        }).catch(e => {
            console.log(e)
        })
    }

    handleInputChange = e => {
        console.log(e)
        let change = {}
        change = this.state.campaign
        change[e.target.name] = e.target.value
        this.setState({
            campaign: change
        })
    }

    render() {
        return(
            <Modal {...this.props} dialogClassName="modal-90w" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Asignar Clientes
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h4>De click en si o no para cambiar el estado de comunicacion con el cliente</h4>
                    <p>
                    <ContactClientsTable 
                        records={this.props.records}
                        campaignId={this.props.campaignId}
                        deleteRow={this.props.deleteRow}
                        contactClients={this.props.contactClients}
                    />
                    </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={this.props.onHide}>Cerrar</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}

export default ContactClientsModal;
